DROP TRIGGER IF EXISTS `after_user_insert`;
delimiter ;;
CREATE TRIGGER `after_user_insert` AFTER INSERT ON `app_user` FOR EACH ROW BEGIN
    -- 插入用户初始记录到日志表
        student_number,
        old_height,
        old_weight,
        new_height,
        new_weight,
        change_time
    )
    VALUES (
        NEW.student_number, 
        NULL,                -- 插入时没有旧值
        NULL,
        NEW.height,
        NEW.weight,
        NOW()
    );
END
;;
delimiter ;

-- ----------------------------
-- Triggers structure for table app_user
-- ----------------------------
DROP TRIGGER IF EXISTS `after_user_update`;
delimiter ;;
CREATE TRIGGER `after_user_update` AFTER UPDATE ON `app_user` FOR EACH ROW BEGIN
    -- 检查height或weight是否发生变化
    IF OLD.height != NEW.height OR OLD.weight != NEW.weight THEN
        -- 插入变更记录到日志表
            student_number,
            old_height,
            old_weight,
            new_height,
            new_weight,
            change_time
        )
        VALUES (
            OLD.student_number,
            OLD.height,
            OLD.weight,
            NEW.height,
            NEW.weight,
            NOW()
        );
    END IF;
END
;;
delimiter ;

